sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {
	
    try {
		var parseATFParameter = function(s) {
			var resp = {};
			var line = s.split(/\r?\n/g);
			for(var i = 0; i < line.length; i++) {
				var val = line[i].split(" = ");
				resp[val[0]] = val[1];
			}
			return resp;
		};
		
		var paramsGR = new GlideRecord('sys_atf_parameter_set');
		paramsGR.get(steps('57c4ae82db721010a2e3880948961949').first_record);

		var param = parseATFParameter(paramsGR.description);
		var testCaseBase64 = gs.base64Encode(JSON.stringify([{
				"name": param["name"],
				"input": {
					"microappNav": param["Tab Name"],
					"microappLocator": param["Element Locator"]
				},
				"expect": {
					"microappClickMessage": param["Expect Message"]
				}
		}]));
		
		var buildParameter = { 
			"testCaseDef":  testCaseBase64
		};
		
		var r = new sn_ws.RESTMessageV2('x_58872_needit.Model-T Build', 'StartJob');
   
		r.setStringParameterNoEscape('JsonParameter', JSON.stringify(buildParameter));
		r.setStringParameterNoEscape('JobName', 'microfrontend_e2e_test');
 	
		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
		outputs.status = 'success';  
	}
	catch(ex) {
		outputs.status = 'failed'; 
		var message = ex.message;
		gs.error(message);
	}

})(outputs, steps, stepResult, assertEqual);
