sys_variable_value.value:
(function execute(inputs, outputs) {
 try { 
   	gs.info("Inputs = " + JSON.stringify(inputs));
   	var r = new sn_ws.RESTMessageV2(inputs.restMessageId, 'StartJob');

   	var buildParameter = {
     "snTableEndpoint": "https://dev87400.service-now.com/api/now/table/x_511928_feature_f_feature_flag_build",
     "snRecordId": inputs.sn_record_id
   	};

   	r.setStringParameterNoEscape('JsonParameter', JSON.stringify(buildParameter));
   	r.setStringParameterNoEscape('JobName', inputs.restMethod);

   	var response = r.execute();
 	var responseBody = response.getBody();
 	var httpStatus = response.getStatusCode();
 	outputs.status = 'success';  
}
catch(ex) {
 	outputs.status = 'failed'; 
 	var message = ex.message;
}
})(inputs, outputs);

